data.styles:
.caption {
  color: color-grey;
  display: block;
  font-size: 0.8em;
  font-style: italic;
  margin-top: 0.5em;
  position: relative;
  text-align: right;
}
hr {
  width: 100%;
  opacity: 0.2;
  margin: 20px auto 20px;
  border: none;
  border-bottom: 1px double #454544;
}
.icon,
.text {
  vertical-align: middle;
  font-weight: 600;
  margin: 0px 3px;
  display: inline-block;
}

